mapper.update:
    	UPDATE BOARD 
    	SET 
	    	BOARD_TITLE = #{boardTitle}
	    	,BOARD_COMMENT = #{boardComment}
    	WHERE 	BOARD_TYPE = #{boardType}
    	AND 	BOARD_NUM  = #{boardNum}

mapper.delete:
DELETE
FROM BOARD 
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM  = #{boardNum}

mapper.select:
SELECT CODE_ID ,CODE_NAME ,BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,BOARD_HITS ,CREATE_TIME FROM ( SELECT CODE_ID ,CODE_NAME ,BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,BOARD_HITS ,BOARD.CREATE_TIME FROM BOARD JOIN COM_CODE ON BOARD.BOARD_TYPE=COM_CODE.CODE_ID
WHERE BOARD_TYPE IN
#{item}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10
